def on_forever():
    # the sound of one heart and other hearts
    if info.life() == 1:
        music.play(music.string_playable("E B C5 A B G A F ", 400),
            music.PlaybackMode.UNTIL_DONE)
    else:
        music.play(music.string_playable("A B G A G F G A ", 500),
            music.PlaybackMode.UNTIL_DONE)
forever(on_forever)
